 * @flow
 * @requires NPM:rsocket-types
 * @requires Counter
 * @requires Timer
 * @exports MetricsSubscriber
 */

'use strict';

import {ISubscription, ISubscriber} from 'rsocket-types';
import Counter from './Counter';
import Timer from './Timer';

/**
 */
export default class MetricsSubscriber<T>
  implements ISubscription, ISubscriber<T> {
  _source: ISubscriber<T>;
  _next: Counter;
  _complete: Counter;
  _error: Counter;
  _cancelled: Counter;
  _timer: Timer;

  _subscription: ISubscription;
  _start: number;

  /**
   * @param {ISubscriber<T>} actual -
   * @param {Counter} next -
   * @param {Counter} complete -
   * @param {Counter} error -
   * @param {Counter} cancelled -
   * @param {Timer} timer -
   */
  constructor(
    actual: ISubscriber<T>,
    next: Counter,
    complete: Counter,
    error: Counter,
    cancelled: Counter,
    timer: Timer,
  ) {
    /** @type {ISubscriber<T>} */
    this._source = actual;
    /** @type {Counter} */
    this._next = next;
    /** @type {Counter} */
    this._complete = complete;
    /** @type {Counter} */
    this._error = error;
    /** @type {Counter} */
    this._cancelled = cancelled;
    /** @type {Timer} */
    this._timer = timer;
  }

  /**
   * @param {ISubscription} s -
   */
  onSubscribe(s: ISubscription) {
    /** @type {ISubscription} */
    this._subscription = s;
    /** @type {number} */
    this._start = new Date().getTime();

    this._source.onSubscribe(this);
  }

  /**
   */
  onNext(t: T) {
    this._next.inc();
    this._source.onNext(t);
  }

  /**
   * @param {Error} t -
   */
  onError(t: Error) {
    this._error.inc();
    this._timer.update(new Date().getTime() - this._start);

    this._source.onError(t);
  }

  /**
   */
  onComplete() {
    this._complete.inc();
    this._timer.update(new Date().getTime() - this._start);
    this._source.onComplete();
  }

  /**
   * @param {number} n -
   */
  request(n: number) {
    this._subscription && this._subscription.request(n);
  }

  /**
   */
  cancel() {
    this._cancelled.inc();
    this._timer.update(new Date().getTime() - this._start);
    this._subscription && this._subscription.cancel();
  }
}

